func (c *current) onStream1(q1, rest interface{}) (interface{}, error) {
	startNode, ok := q1.(*ast.Node)
	if !ok {
		return nil, nil
	}
	if len(rest.([]interface{})) == 0 {
		return startNode, nil
	}
	finalNode := &ast.Node{
		NodeType: ast.NodeAnd,
		Left:     startNode,
		Right:    &ast.Node{},
	}
	ptr := finalNode
	for index, query := range rest.([]interface{}) {
		if index == len(rest.([]interface{}))-1 {
			ptr.Right = query.(*ast.Node)
		} else {
			ptr.Right = &ast.Node{
				NodeType: ast.NodeAnd,
				Left:     query.(*ast.Node),
				Right:    &ast.Node{},
			}
			ptr = ptr.Right
		}
	}
	return finalNode, nil
}

